module.exports = (client, QwertylAyar) => {
    const reqEvent = (event) => require(`${process.cwd()}/${QwertylAyar.eventKlasör}/${event}`);
    client.on("ready", () => reqEvent("hazır")(client, "message"));
    client.on("message", reqEvent("mesaj"));
    client.on("guildMemberAdd", reqEvent("biriGirdiğinde"))
    client.on("guildMemberRemove", reqEvent("biriÇıktığında"));
}